sp_widget.script:
(function() {
	data.votes = [];

	var count = new GlideAggregate('x_393642_project_2_vote');
	count.addAggregate('COUNT', 'u_vote');
	count.groupBy('u_vote');
	count.query();

	while(count.next()){
		var voteTotal = count.getAggregate('COUNT', 'u_vote');
		data.votes.push({
			voteCount: voteTotal,
			name: count.u_vote.getDisplayValue()
		});


	}


})();
